import React, { useState } from 'react';

function Shop() {
  const [filter, setFilter] = useState('all');

  const products = [
    { id: 1, name: 'Silk Midi Dress', price: 'R1,599', category: 'dresses', size: ['XS', 'S', 'M', 'L'], image: 'https://images.unsplash.com/photo-1595777457583-95e059d581b8?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 2, name: 'Linen Wrap Dress', price: 'R1,349', category: 'dresses', size: ['S', 'M', 'L', 'XL'], image: 'https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 3, name: 'Cotton Sundress', price: 'R1,169', category: 'dresses', size: ['XS', 'S', 'M'], image: 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 4, name: 'Chiffon Maxi', price: 'R1,699', category: 'dresses', size: ['S', 'M', 'L'], image: 'https://images.unsplash.com/photo-1566479179817-c0b5b4b4b1e8?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 5, name: 'Waist Trainer', price: 'R629', category: 'accessories', size: ['S', 'M', 'L'], image: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 6, name: 'Premium Waist Trainer', price: 'R809', category: 'accessories', size: ['XS', 'S', 'M', 'L', 'XL'], image: 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 7, name: 'Sports Bra & Leggings Set', price: 'R899', category: 'gym-sets', size: ['XS', 'S', 'M', 'L'], image: 'https://images.unsplash.com/photo-1506629905607-d405b7a30db9?w=400&h=500&fit=crop&auto=format&q=80' },
    { id: 8, name: 'Yoga Set', price: 'R749', category: 'gym-sets', size: ['S', 'M', 'L', 'XL'], image: 'https://images.unsplash.com/photo-1544966503-7cc5ac882d5f?w=400&h=500&fit=crop&auto=format&q=80' }
  ];

  const filteredProducts = filter === 'all' ? products : products.filter(product => product.category === filter);

  return (
    <div className="section">
      <div className="container">
        <h1>Shop All</h1>

        {/* Filter Buttons */}
        <div style={{display: 'flex', justifyContent: 'center', gap: '1rem', marginBottom: '2rem', flexWrap: 'wrap'}}>
          <button 
            className="btn"
            onClick={() => setFilter('all')}
            style={{background: filter === 'all' ? '#1a1a1a' : 'white', color: filter === 'all' ? 'white' : '#666', border: '1px solid #e5e5e5'}}
          >
            All
          </button>
          <button 
            className="btn"
            onClick={() => setFilter('dresses')}
            style={{background: filter === 'dresses' ? '#1a1a1a' : 'white', color: filter === 'dresses' ? 'white' : '#666', border: '1px solid #e5e5e5'}}
          >
            Dresses
          </button>
          <button 
            className="btn"
            onClick={() => setFilter('accessories')}
            style={{background: filter === 'accessories' ? '#1a1a1a' : 'white', color: filter === 'accessories' ? 'white' : '#666', border: '1px solid #e5e5e5'}}
          >
            Accessories
          </button>
          <button 
            className="btn"
            onClick={() => setFilter('gym-sets')}
            style={{background: filter === 'gym-sets' ? '#1a1a1a' : 'white', color: filter === 'gym-sets' ? 'white' : '#666', border: '1px solid #e5e5e5'}}
          >
            Gym Sets
          </button>

        </div>

        {/* Products Grid */}
        <div className="product-grid">
          {filteredProducts.slice(0, 8).map((product, index) => {
            const scarcityMessages = ['Only 2 left!', 'Only 1 left!', 'Low stock', 'Almost gone!'];
            const socialProof = ['43 people viewed today', '67 people viewed today', '29 people viewed today', '91 people viewed today'];


            return (
              <div key={product.id} className="product-card">

                <div className="product-image">
                  <img src={product.image} alt={product.name} />
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="price">{product.price}</p>
                  <p style={{fontSize: '0.8rem', color: '#FF6B6B', fontWeight: '600', marginBottom: '4px'}}>
                    {scarcityMessages[index % 4]}
                  </p>
                  <p style={{fontSize: '0.8rem', color: '#8B7B93', marginBottom: '8px'}}>
                    👁️ {socialProof[index % 4]}
                  </p>
                  <p style={{fontSize: '0.9rem', color: '#999', marginBottom: '8px'}}>
                    Your sizes: {product.size.join(', ')}
                  </p>
                  <button className="btn" style={{marginTop: '10px', width: '100%'}}>
                    Make It Mine
                  </button>
                </div>
              </div>
            );
          })}
        </div>
        {filteredProducts.length > 8 && (
          <div style={{textAlign: 'center', marginTop: '2rem'}}>
            <button className="btn" style={{padding: '12px 30px'}}>Load More</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Shop;
